INSERT INTO `activities_requirement` VALUES (21,51,'Fitness Level','Moderate to High','This climb involves ascending 1,200 steps. Participants should have a reasonable level of fitness and be comfortable with steep climbs.',1,'#FF6B6B','2026-02-20 14:57:31',NULL,'2026-02-20 14:57:31',NULL,NULL,NULL),(22,51,'Minimum Age','8 years','Children below 8 years are not recommended due to the strenuous nature of the climb. Children must be accompanied by adults.',1,'#4ECDC4','2026-02-20 14:57:31',NULL,'2026-02-20 14:57:31',NULL,NULL,NULL),(23,51,'Footwear','Closed-toe shoes required','Sturdy, comfortable walking shoes or hiking boots with good grip are mandatory for safety on the rock surface and steps.',1,'#45B7D1','2026-02-20 14:57:31',NULL,'2026-02-20 14:57:31',NULL,NULL,NULL),(24,51,'Health Advisory','Not recommended for certain conditions','Not recommended for individuals with heart conditions, respiratory issues, severe back/knee problems, or fear of heights. Pregnant women should consult their doctor.',1,'#96CEB4','2026-02-20 14:57:31',NULL,'2026-02-20 14:57:31',NULL,NULL,NULL),(25,51,'Recommended Items','Water, hat, sunscreen','Bring at least 1L of water per person, sun protection (hat, sunscreen), and a camera. Light snacks recommended.',1,'#FFE194','2026-02-20 14:57:31',NULL,'2026-02-20 14:57:31',NULL,NULL,NULL),(26,51,'Weather Advisory','Morning climb recommended','Best to climb early morning (before 10 AM) to avoid midday heat and potential afternoon rain. The rock can be slippery when wet.',1,'#B0A8B9','2026-02-20 14:57:31',NULL,'2026-02-20 14:57:31',NULL,NULL,NULL),(27,51,'Dress Code','Modest, comfortable clothing','Wear comfortable, breathable clothing suitable for hiking. Shoulders and knees should be covered as a mark of respect at the historical site.',1,'#FFA07A','2026-02-20 14:57:31',NULL,'2026-02-20 14:57:31',NULL,NULL,NULL),(28,51,'Prohibited Items','No drones or large bags','Drones, large backpacks, and tripods are not allowed on the rock. Only small bags/camera bags permitted.',1,'#DC143C','2026-02-20 14:57:31',NULL,'2026-02-20 14:57:31',NULL,NULL,NULL),(29,51,'Time Required','2.5 - 3.5 hours total','Plan for approximately 2.5-3.5 hours for the complete experience including climbing, exploring the summit, and descending.',1,'#9B59B6','2026-02-20 14:57:31',NULL,'2026-02-20 14:57:31',NULL,NULL,NULL);
